// Interface defines the contract
public interface IShape
{
    double CalculateArea();
}

// Existing implementation
public class Circle : IShape
{
    public double Radius { get; set; }
    public double CalculateArea() => Math.PI * Radius * Radius;
}

// New implementation added without changing existing code
public class Rectangle : IShape
{
    public double Width { get; set; }
    public double Height { get; set; }
    public double CalculateArea() => Width * Height;
}

// AreaCalculator uses the interface, no changes needed for new shapes
public class AreaCalculator
{
    public double TotalArea(List<IShape> shapes)
    {
        return shapes.Sum(shape => shape.CalculateArea());
    }
}

// âŒ Violates OCP
public class TaxService
{
    public decimal CalculateTax(string country, decimal amount)
    {
        if (country == "US") return amount * 0.07m;
        else if (country == "UK") return amount * 0.20m;
        else return 0;
    }
}

// âœ… Follows OCP using interface
public interface ITaxCalculator
{
    decimal CalculateTax(decimal amount);
}

public class USTaxCalculator : ITaxCalculator
{
    public decimal CalculateTax(decimal amount) => amount * 0.07m;
}

public class UKTaxCalculator : ITaxCalculator
{
    public decimal CalculateTax(decimal amount) => amount * 0.20m;
}

public class TaxService
{
    private readonly ITaxCalculator _calculator;

    public TaxService(ITaxCalculator calculator)
    {
        _calculator = calculator;
    }

    public decimal GetTax(decimal amount) => _calculator.CalculateTax(amount);
}

// Add a new country: Just create a new class implementing ITaxCalculator

// âœ… LSP Example
public class Bird
{
    public virtual void Fly() { Console.WriteLine("Flying"); }
}

public class Sparrow : Bird
{
    public override void Fly() { Console.WriteLine("Sparrow flying"); }
}

// âŒ Violates LSP - Penguin can't fly but inherits Fly()
public class Penguin : Bird
{
    public override void Fly() { throw new NotSupportedException("Penguins can't fly"); }
}

// âœ… Fix by refactoring
public abstract class Bird { }

public interface IFlyingBird
{
    void Fly();
}

public class Sparrow : Bird, IFlyingBird
{
    public void Fly() { Console.WriteLine("Sparrow flying"); }
}

public class Penguin : Bird
{
    // No Fly method, so no broken expectation
}

// âŒ Violates LSP
public class Rectangle
{
    public virtual int Width { get; set; }
    public virtual int Height { get; set; }
    public int Area() => Width * Height;
}

public class Square : Rectangle
{
    public override int Width
    {
        set { base.Width = base.Height = value; }
    }
    public override int Height
    {
        set { base.Width = base.Height = value; }
    }
}

// Client expecting independent width/height breaks when used with Square
void PrintArea(Rectangle rect)
{
    rect.Width = 5;
    rect.Height = 10;
    Console.WriteLine(rect.Area()); // âœ… Expected 50, âŒ gets 100 for Square
}

// âœ… Fix: Don't force inheritance where behavior differs
public interface IShape
{
    int Area();
}

public class Rectangle : IShape
{
    public int Width { get; set; }
    public int Height { get; set; }
    public int Area() => Width * Height;
}

public class Square : IShape
{
    public int Side { get; set; }
    public int Area() => Side * Side;
}

