public class MySQLDatabase
{
    public void SaveData(string data) => Console.WriteLine("Saving to MySQL");
}

public class DataService
{
    private MySQLDatabase _db = new MySQLDatabase();
    public void Save(string data) => _db.SaveData(data);
}

// âœ… Follows DIP using interface
public interface IDatabase
{
    void SaveData(string data);
}

public class MySQLDatabase : IDatabase
{
    public void SaveData(string data) => Console.WriteLine("Saving to MySQL");
}

public class DataService
{
    private readonly IDatabase _db;

    public DataService(IDatabase db)
    {
        _db = db;
    }

    public void Save(string data) => _db.SaveData(data);
}

// Now you can inject any database implementation without changing DataService

// âœ… DIP: Depend on abstraction
public interface IEmailService
{
    void SendEmail(string message);
}

public class EmailService : IEmailService
{
    public void SendEmail(string message) => Console.WriteLine($"Sending: {message}");
}

// âœ… Dependency Injection: Injecting the dependency
public class Notification
{
    private readonly IEmailService _emailService;

    // Constructor Injection
    public Notification(IEmailService emailService)
    {
        _emailService = emailService;
    }

    public void Notify(string msg) => _emailService.SendEmail(msg);
}

// Usage
var notification = new Notification(new EmailService());
notification.Notify("Hello!");

