// Strategy Pattern example for OCP
public interface IDiscountStrategy
{
    decimal ApplyDiscount(decimal price);
}

public class NoDiscount : IDiscountStrategy
{
    public decimal ApplyDiscount(decimal price) => price;
}

public class NewYearDiscount : IDiscountStrategy
{
    public decimal ApplyDiscount(decimal price) => price * 0.9m;
}

public class CheckoutService
{
    private readonly IDiscountStrategy _strategy;
    public CheckoutService(IDiscountStrategy strategy)
    {
        _strategy = strategy;
    }

    public decimal GetFinalPrice(decimal price) => _strategy.ApplyDiscount(price);
}

// Add new discount logic without touching CheckoutService â†’ OCP applied

