// âœ… Factory example for OCP
public interface IPaymentGateway { void Pay(); }

public class StripeGateway : IPaymentGateway
{
    public void Pay() => Console.WriteLine("Paid with Stripe");
}

public class RazorpayGateway : IPaymentGateway
{
    public void Pay() => Console.WriteLine("Paid with Razorpay");
}

public class PaymentGatewayFactory
{
    public IPaymentGateway Create(string provider)
    {
        return provider switch
        {
            "Stripe" => new StripeGateway(),
            "Razorpay" => new RazorpayGateway(),
            _ => throw new NotSupportedException()
        };
    }
}

// This allows new gateways to be added without modifying existing code (OCP)
